import React, { useEffect, useState } from 'react';

import useWebSocket from 'react-use-websocket';


function App() {
  const stats = useStats()
  return (
    <div className="h-full w-full bg-gray-50">
      <>
        {Object.entries(stats.records).map((tap) => {
          console.log(tap)
          return (<div> Tap fuck</div>)
        })}
      </>
    </div >
  );
}

interface Stats {
  total_pulses: number,
  total_ounces: number,
  records: { [tap: number]: Record[] },
}

interface Record {
  timestamp: number,
  tap: number,
  pulses: number,
  ounces: number,
}

const addr = "ws://192.168.100.126:8080/stats"
function useStats(): Stats {
  const { lastJsonMessage } = useWebSocket(addr, {
    onOpen: () => console.log('opened'),
    shouldReconnect: (_) => true,
  });

  if (!lastJsonMessage) {
    return { total_pulses: 0, total_ounces: 0, records: [] }
  }
  return lastJsonMessage as unknown as Stats
}

export default App;
