import React, { useEffect, useState } from 'react';

import { w3cwebsocket as W3CWebSocket } from "websocket";

function App() {
  const stats = useStats()
  return (
    <div className="h-full w-full bg-gray-50">
      <>
        {stats.records.map((_, v) => (
          <div>Hello</div>
        ))}
      </>
    </div>
  );
}

interface Stats {
  total_pulses: number,
  total_ounces: number,
  records: Record[],
}

interface Record {
  timestamp: number,
  tap: number,
  pulses: number,
  ounces: number,
}

const addr = "ws://192.168.100.126:8080/status"
function useStats() {
  const [ws, setWs] = useState(new W3CWebSocket(addr));
  const [stats, setStats] = useState({ total_pulses: 0, total_ounces: 0, records: [] });
  // Initial WS connection + retries
  useEffect(() => {
    const id = setInterval(() => {
      if (ws.readyState === 3) {
        console.log("oh shit")
        setWs(new W3CWebSocket(addr));
      }
    }, 5000);

    return () => clearInterval(id)
  }, [ws]);

  useEffect(() => {
    ws.onopen = () => console.log("Connected to WebSocket");
    ws.onmessage = (payload: any) => {
      const data: Stats = JSON.parse(payload.data);
      setStats(data);
    }
  });
  return stats
}

export default App;
