import React, { useEffect, useState } from 'react';

import useWebSocket from 'react-use-websocket';


function App() {
  const stats = useStats()
  return (
    <div className="h-screen w-screen bg-gray-200">
      <div className="flex flex-row">
        <div className="flex flex-col">
          {Object.entries(stats.records).map((item) => {
            const name = item[0]
            const records = item[1]
            return (
              <div className="bg-blue-300 mx-auto p-4">
                Tap {name}
              </div>
            )
          })}
        </div>
      </div>
    </div >
  );
}

interface Stats {
  total_pulses: number,
  total_ounces: number,
  records: { [tap: number]: Record[] },
}

interface Record {
  timestamp: number,
  tap: number,
  pulses: number,
  ounces: number,
}

const addr = "ws://192.168.100.126:8080/stats"
function useStats(): Stats {
  const { lastJsonMessage } = useWebSocket(addr, {
    onOpen: () => console.log('opened'),
    shouldReconnect: (_) => true,
  });

  if (!lastJsonMessage) {
    return { total_pulses: 0, total_ounces: 0, records: [] }
  }
  return lastJsonMessage as unknown as Stats
}

export default App;
