import React, { useEffect, useState } from 'react';

import useWebSocket from 'react-use-websocket';


function App() {
  const stats = useStats()
  return (
    <div className="h-screen w-screen bg-blue-200">
      <div className="grid grid-cols-6 grid-rows-6 h-screen">
        {Object.entries(stats.records).map((item) => {
          const name = item[0]
          const records = item[1]
          let classes = ""
          if (name === "1") {
            classes = "col-start-2 col-end-4"
          } else if (name === "2") {
            classes = "col-start-4 col-end-6"
          }
          return (
            <div className={"row-start-2 row-end-5 bg-gray-100 border-gray-800 rounded-md shadow-md h-auto m-4 p-4" + " " + classes}>
              Tap {name}
            </div>
          )
        })}
      </div>
    </div >
  );
}

interface Stats {
  total_pulses: number,
  total_ounces: number,
  records: { [tap: number]: Record[] },
}

interface Record {
  timestamp: number,
  tap: number,
  pulses: number,
  ounces: number,
}

const addr = "ws://192.168.100.126:8080/stats"
function useStats(): Stats {
  const { lastJsonMessage } = useWebSocket(addr, {
    onOpen: () => console.log('opened'),
    shouldReconnect: (_) => true,
  });

  if (!lastJsonMessage) {
    return { total_pulses: 0, total_ounces: 0, records: [] }
  }
  return lastJsonMessage as unknown as Stats
}

export default App;
